actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  "input": [
    {
      "carrier": {
        "id": appsmith.store.getCarrierByCodeResultId
      },
      "user": {
					"id":GetUserSites.data.data.getUser.id,
        "username": appsmith.user.email
      },
      "storageLocation": {
        "id": appsmith.store.getWarehouseByNameResultId,
        "name": appsmith.store.getWarehouseByNameResult
      },
    }
  ]
},
    query: `
mutation moveCarrierToBin($input:[InventoryTransactionInput!]!){
moveCarrierToBin(input:$input){
carrier{
code
}
storageLocation{
name
}
}
} `
})
}}

