actionConfiguration.body:
{{
JSON.stringify({
    variables:  {
  "input": [
    {
      "carrier": {
						"id": appsmith.store.queryCarrierID,
        "code": appsmith.store.queryCarrierCode
      },
      "storageLocation": {
						"id":appsmith.store.queryBinId,
        "name": appsmith.store.queryBinName
      },
      "user": {
						"id":queryUserSites.data.data.getUser.id,
        "username": appsmith.user.email
      },


    }
  ]
},
    query: `
mutation($input:[InventoryTransactionInput!]!){
  moveCarrierToBin(input:$input){
    carrier{
      code
    }
    storageLocation{
      name
    }
  }
}
    `
})
}}

