actionConfiguration.body:
{{
JSON.stringify({
    variables:  {
  "input": [
    {
      "material": {
        "code": GetStockOnHandByCarrierTable.selectedRow.materialCode
      },
      "quantity": parseFloat(NewCarrierQuantityInput.text),
      "from": {
        "carrier": {
							"id":appsmith.store.queryMoveCarrierId,
          "code": appsmith.store.queryMoveCarrierCode
        }
      },
      "to": {
        "carrier": {
							"id":appsmith.store.queryNewCarrierId,
          "code": appsmith.store.queryNewCarrierCode
        }
      },
      "materialUse": "Transferred",
      "owner": {
        "id": "0x641ab"
      }
    }
  ]
},
    query: `
mutation($input:[AddInventoryTransactionInput!]!){
  addInventoryTransactions(input:$input){
    carrier{
      code
    }
  }
}
    `
})
}}

