{{
JSON.stringify({
    variables: {
			input: {
				filter: {id: listInboundDeliveries.selectedItem.id},
				set: {
					status:"RECEIVED",dateTimeReceived:moment()
				}
			}
		},
    query: `
mutation UpdateDelivery($input:UpdateDeliveryInput!) {
  updateDelivery(input:$input){
    numUids
  }
}
    `
})
}}

