actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  input: [
  	{
			dateTimeCreatedUTC: moment(),
      customerReference: inputAddShipmentReference.text,
      warehouse: {id: selectAddShipmentWarehouse.selectedOptionValue},
      transportCompanyShipmentId: inputAddShipmentDeliveryRef.text,
      consignmentNumber: inpConsignmentNumber.text,
      type: "INBOUND",
			delivered: {"id": listInboundDeliveries.selectedItem.id},
      order: {
        customerOrderNumber: inpOrderNumber.text,
        orderType: "PURCHASE_ORDER",
        createdDateTime: moment(),
        status: "CREATED"
      }
  	}
  ]
},
    query: `
mutation AddShipmentNewOrder($input:[AddShipmentInput!]!) {
  addShipment(input:$input) {
    shipment {
      id
    }
  }
}
    `
})
}}

