actionConfiguration.body:
() => {
		var ret = []
		for (let i = 0; i < GetShipmentDetail.data.data.getShipment.items.length; i ++) {
			let item = GetShipmentDetail.data.data.getShipment.items[i]
			let line = {"id": item.id}
			if (item.material != null) {
				line.material = item.material.code
			} else {
				line.material = ""
			}
			if (item.bin != null) {
				line.bin = item.bin.name
			} else {
				line.bin = ""
			}
			if (item.plannedQuantity != null) {
				line.quantity = item.plannedQuantity
			} else {
				line.quantity = 0
			}
			if (item.trackingDetailsArticleId != null) {
				line.trackingId = item.trackingDetailsArticleId
			} else {
				line.trackingId = ""
			}
			ret.push(line)
		}
		return ret
	}

