actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			carrierId:tblCarriers.selectedRow.id
		},
    query: `
query( $carrierId:ID!){
  getStockOnHand(filter:{carrier:{id:$carrierId},groupBy:{subLot:true, material:true, carrier:true, storageLocation:true}}){
    materialSubLot{code}
    material{code}
    carrier{code}
    storageLocation{name}
    quantity
  }
}
    `
})
}}

