actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			sublotCode:inpBarcodeScan.text
		},
    query: `
query( $sublotCode:String!){
  getStockOnHand(filter:{materialSubLot:{code:$sublotCode},groupBy:{subLot:true, material:true, carrier:true, storageLocation:true}}){
    materialSubLot{code}
    material{code}
    carrier{code}
    storageLocation{name}
    quantity
  }
}
    `
})
}}

