actionConfiguration.body:
{{
JSON.stringify({
    variables:  {
  "input": [
    {
			"material": {
              "code": appsmith.store.queryMateriabSubLotmaterialCode
            },
     	"materialSubLot":{
        "code":appsmith.store.queryMaterialSubLotCode,
        "id":appsmith.store.queryMaterialSubLotId
      },
      "quantity": 1,
      "to": {
        "carrier": {
          "id": appsmith.store.queryNewCarrierId,
          "code": appsmith.store.queryNewCarrierCode
        }
      },
      "materialUse": "Transferred",
      "owner": {
        "id": "0x641ab"
      }
    }
  ]
},
    query: `
mutation($input:[AddInventoryTransactionInput!]!){
  addInventoryTransactions(input:$input){
    carrier{
      code
    }
  }
}
    `
})
}}

