actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			warehouseId:selectWarehouse.selectedOptionValue
		},
    query: `
query ($warehouseId:ID!){
  queryCarrier @cascade {
    code
    status{code}
    storageLocation{
      label
      name
      id
      warehouse:parent(filter:{id:[$warehouseId]}){
        label
        name
        id
      }
    }
  }
}
    `
})
}}

