actionConfiguration.body:
{{
JSON.stringify({
    variables:  {
	"input": appsmith.user.email
},
    query: `
query($input:String){
  getUser(username:$input){
			id
    hasRole{
      responsibilities(filter:{equipmentLevel:{eq:Site}}){
        value: id
        label: name
        equipmentLevel
      }
    }
  }
}
    `
})
}}

