actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: [
    {
      material: {code: GetShipmentItemDetail.data.data.getShipmentItem.material.code},
      materialUse: "Received",
      owner: {id: GetCustomers.data.data.queryPartner[0].value},
			user: {id: GetUserSites.data.data.getUser.id},
      quantity: _.toInteger(inputBulkQuantity.text),
      from: {
        shipment: {id: tblShipmentList.selectedRow.id},
        shipmentItem: {id: selectShipmentItem.selectedOptionValue}
      },
      to: {
				status: {code: bulkSelectStatus.selectedOptionLabel},
				storageLocation: {name: bulkSelectBin.selectedOptionLabel}
      }
    }
  ]
		},
    query: `
mutation ReceiveUntracked($input:[AddInventoryTransactionInput!]!) {
	addInventoryTransactions(input:$input){
    material{code},
    storageLocation{name},
    quantity
  }
}
    `
})
}}

