actionConfiguration.body:
() => {
		var items = GetShipmentWithItems.data.data.getShipment.items
		var itemKey =_.findKey(items,{'id':selectShipmentItem.selectedOptionValue})
		var item = items[itemKey]
		//return item
		return (item.material.inventoryHandlingPolicy.trackBySerialNumber ? 'SERIALISED':item.material.inventoryHandlingPolicy.trackByCarrier ? 'CARRIER':'BULK');
	}

